import mathdef MaxRectange(lenstr, string):    """    Base on the original string, it will be divided into    a rectange with the max column and row    """    MAXROW = math.sqrt(lenstr)    if MAXROW > int(math.sqrt(lenstr)):        MAXCOL = int(math.sqrt(lenstr)) + 1        MAXROW = int(math.sqrt(lenstr))        # print(MAXROW,MAXCOL) # Check MAXROW and MAXCOL        return (MAXROW,MAXCOL)    else:        MAXCOL = int(math.sqrt(lenstr))        MAXROW = int(math.sqrt(lenstr))        # print(MAXROW, MAXCOL) # Check MAXROW and MAXCOL        return (MAXROW, MAXCOL)def addListWord(row, col, lenstr, string):    """    :param row: MaxROW from MaxRectange function    :param col: MaxCOL from MaxRectange function    :param string: the original string without space    :return: new listword []    """    count = 0    word = ""    listword = []  # Add the new word after divided in term of MAXCOL    SupplySpace = col * row - lenstr  # Add more space to word if length of string is not equal the square    for i in string:        # print(i,end="")        word += i        count += 1        if count % col == 0:            # print()            listword.append(word)            word = ""    if SupplySpace > 0:        listword.append(word + ((" ") * SupplySpace))  # Add more space    print(listword)  # Check the new list word    return listworddef encode(length, listword):    """    Encoded the message is obtained by    reading down the column going left to right    """    for i in range(0,length):        for word in listword:            print(word[i], end="")        print()def main():    #text = "if man was meant to stay on the ground god would have given us roots"    text = input("Enter: ")    newstring = text.replace(" ","")    lenstr = len(newstring)    # print(lenstr) # Check length of newstring    row, col = MaxRectange(lenstr, newstring)    listword = addListWord(row, col, lenstr, newstring)    encode(col, listword)main()